import React, { Fragment } from 'react';
import classes from './FlightsForm.module.css';

const FlightsForm = () => {
  const submitHandler = () => {
    console.log('hi');
  };
  return (
    <Fragment>
        <div className={classes.main}>
        <div className={classes.radio}>
            <input type='radio' value='roundtrip' name='roundtrip'/>roundtrip <input type='radio' value='one way' name='one way'/>One way
        </div>
        <form className={classes.form} onSubmit={submitHandler}>

        <div>
          <label>From: </label>
          <input type="text" placeholder="city" />
        </div>
        <div>
          {' '}
          <label>To: </label>
          <input type="text" placeholder="city" />
        </div>
        <div>
          <label>Departure: </label>
          <input type="date" placeholder="Departure date" />
        </div>
        <div>
          <label>Return: </label>
          <input type="date" placeholder="Return" />
        </div>
        <div>
          {' '}
          <label>Passengers: </label>
          <input type="number" placeholder="Passengers" />
        </div>
      </form>
      <div className={classes.checkBox}>
        <div> <input type='checkbox' value='add' name='add'/>Add near by Airports</div>
        <div></div>

        <input type='checkbox' value='add' name='add'/>Non- stop flights only
      </div>
        </div>

    </Fragment>
  );
};

export default FlightsForm;
